class Background
{
    constructor()
    {
        this.x = 0
        this.y = 0
        this.width = canvas.width
        this.height = canvas.height
    }

    draw(CAM_Y)
    {
        var color = Math.round(-CAM_Y / 50)
        color = 255 - color
        c.fillStyle = "rgb(" + String(117 - color) + ", " + String(216 - color) + ", " + String(255 - color) + ")"
        c.fillRect(this.x, this.y, this.width, this.height)
    }

}

class Tile
{
    constructor(typex, typey, x, y)
    {
        this.typex = typex
        this.typey = typey
        this.x = x
        this.y = y
    }

    draw()
    {
        //draw the image based on its type (uses a spritesheet so both an x and y are required)
        //draw it at its position + the Cameras position
        c.drawImage(tilesheet, this.typex * 64 + 1, this.typey * 64 + 1, 62, 62, this.x * 64 + CAM_X, this.y * 64 + CAM_Y, 64, 64)
        
    }
    update()
    {
        /*
        I don't think I need this after looking through my old code but if something breaks I need to come back here
        
        if (this.type == 1)
        {
            if (this.x * 64 + CAM_X <= -64)
            {
                this.x += 45
            }
            if (this.x * 64 + CAM_X >= 44 * 64)
            {
                this.x -= 45
            }
            if (this.y * 64 + CAM_Y <= -64)
            {
                this.y += 21
            }
            if (this.y * 64 + CAM_Y >= 20 * 64)
            {
                this.y -= 21
            }
        }*/
        
    }
    SetType(type)
    {
        this.typex = type
    }
}


class Button
{
    constructor(x, y, width, height, text, color, active, type)
    {
        this.x = x
        this.y = y
        this.width = width
        this.height = height
        this.text = text
        this.color = color
        this.active = active
        this.type = type
    }
    draw()
    {
        if (this.active)
        {
            c.fillStyle = this.color
            c.fillRect(this.x, this.y, this.width, this.height)
            c.font = String(this.width / (this.text.length + 5) + 4) + "pt Arcade Normal"
            c.fillStyle = 'black'
            var textWidth = c.measureText(this.text).width
            c.fillText(this.text, this.x + this.width / 2 - textWidth / 2, this.y + this.height / 2 + this.height / 10, this.width)
        }
        
    }
    update(mousex, mousey, buttons)
    {
        //if the mouse is touching the button, then based on the type of the button, if the mouse is down perform the corresponding action
        if (this.x + this.width + 20 > mousex && this.x < mousex  && this.y + this.height + 20 > mousey && this.y < mousey && this.active)
        {
            if (this.type == 1) //play button
            {
                if (mousedown)
                {
                    menu = 1
                    PlayFromStart()
                }
                this.color = 'lightskyblue'
            }
            if (this.type == 2)
            {
                
            }
            if (this.type == 3)
            {
                if (mousedown) //load button
                {
                    
                    buttons.forEach(button=>
                        {
                            button.active = false
                        })
                    edit = false
                    mousedown = false
                }
                this.color = 'tan'
            }
            if (this.type == 4)
            {
                if (mousedown) //help button
                {
                    alert("WASD to move, E to edit, Esc to exit play mode, 1-7 for the different blocks, 0 for eraser")
                    alert("The player wins by bringing the green flag to the grey flag")
                    mousedown = false
                }
                this.color = 'tan'
            }
            
        }
        else if (this.active) //this sets the standard color of the button
        {
            if (this.type < 5)
            {
                this.color = 'cornflowerblue'
            }
            
        }
    }
}


class Flag
{
    constructor()
    {
        this.x = 0
        this.y = 0
        this.active = false
    }
    draw()
    {
        if (this.active)
        {
            c.drawImage(tilesheet, 3 * 64 + 1, 0 * 64 + 1, 62, 62, this.x, this.y, 40, 40)
        }
        
    }
    update(playerx, playery)
    {
        if (this.active) //if the player has the flag, follow the player, otherwise don't do anything
        {
            this.x = playerx
            this.y = playery
        }
    }
}

class Mario
{
    constructor()
    {
        this.x = canvas.width / 2 - 20 //position to draw the sprite (center of the screen)
        this.lx = 0 //position in the level (level x)
        this.ly = 0 //position in the level (level x)
        this.espeed = 25 //edit mode speed (not for actual game)
        this.y = 640 - 256 - 64//position to draw the sprite
        this.xvel = 0
        this.yvel = 0
        this.grounded = false // if true allows player to jump
        this.speed = 12 //top speed
        this.friction = .8
    }

    draw()
    {
        c.drawImage(player, this.x, this.y, 40, 40)
    }
    update(tiles)
    {   
        
            if (keys.left.pressed)
            {
                this.xvel += 1
            
            }
            else if (keys.right.pressed)
            {
                this.xvel -= 1   
            }
            else if (touches)
            {
                
            }
            else if (this.xvel > this.friction)
            {
                this.xvel -= this.friction
            }
            else if (this.xvel < -this.friction)
            {
                this.xvel += this.friction
            }
            else
            {
                this.xvel = 0
            }
            if (keys.up.pressed && this.grounded == true)
            {
                this.yvel = 21
                this.grounded = false
            }


            if (this.xvel >= this.speed)
            {
                this.xvel = this.speed
            }
            if (this.xvel <= this.speed * -1)
            {
                this.xvel = this.speed * -1
            }

            this.lx = CAM_X




            this.ly = CAM_Y

            this.yvel -= 1



            this.grounded = false




            var steps = Math.abs(this.xvel) + Math.abs(this.yvel)



            for (var i = 0; i < steps; i++)
            {

                CAM_Y += this.yvel / steps
                tiles.forEach(tile => {
                    if (tile.typex != 3 && tile.typex != 4 && tile.typex != 5) //if the tile isn't the flag, end, or spikes, do normal collisions
                    {
                        if (this.x + 40 > tile.x * 64 + CAM_X && this.x < tile.x * 64 + CAM_X + 64 && this.y + 40 > tile.y * 64 + CAM_Y && this.y < tile.y * 64 + CAM_Y + 64 && tile.typex != -1 && tile.typex != -3)
                        {


                            if (tile.typex == 2) //if the tile is ice, lower the friction and raise the top speed
                            {
                                this.friction = .2
                                this.speed = 15
                            }
                            else //otherwise set it back to the normal values
                            {
                                this.friction = .8
                                this.speed = 12
                            }

                            CAM_Y = this.ly //set the cameray to the levely of the player (because the camera always follows the player)
                        
                            if (this.yvel < 0) //if the yvel is 0 (and the player is touching a tile) the player can jump
                            {
                                this.grounded = true
                            }


                            this.yvel = 0 //after checking for a yvel of 0, set the yvel to 0 | This must be done after because otherwise you could jump off walls for 1 frame
                            if (tile.typex == 6 && this.grounded) // if the player is touching a jump block
                            {
                                this.yvel = 23 //random value that I found that worked well
                                this.grounded = false
                            }
                        
                        }
                    }
                    else if (tile.typex == 3) //if we are touching the flag, set it to active
                    {
                        if (this.x + 40 > tile.x * 64 + CAM_X && this.x < tile.x * 64 + CAM_X + 64 && this.y + 40 > tile.y * 64 + CAM_Y && this.y < tile.y * 64 + CAM_Y + 64 && tile.typex != -1)
                        {
                            flag.active = true
                            tile.typex = -3
                        }
                    }
                    else if (tile.typex == 4 && flag.active == true) // if we are touching the end and we have the flag, restart the level
                    {
                        if (this.x + 40 > tile.x * 64 + CAM_X && this.x < tile.x * 64 + CAM_X + 64 && this.y + 40 > tile.y * 64 + CAM_Y && this.y < tile.y * 64 + CAM_Y + 64 && tile.typex != -1)
                        {
                            menu = 2
                        }

                    }
                    else if (tile.typex == 5 && !this.grounded)
                    {
                        if (this.x + 40 > tile.x * 64 + CAM_X && this.x < tile.x * 64 + CAM_X + 64 && this.y + 30 > tile.y * 64 + CAM_Y && this.y < tile.y * 64 + CAM_Y + 64 && tile.typex != -1)
                        {
                            flag.active = false
                            CAM_X =  0 + 64 + 64
                            CAM_Y = -640 - 64
                            tiles.forEach(tile =>{
                                if (tile.typex == -3)
                                {
                                    tile.typex = 3
                                }
                            })
                            this.xvel = 0
                            this.yvel = 0
                        }
                    }

                })

                CAM_X += this.xvel / steps
                tiles.forEach(tile => {
                    if (tile.typex != 3 && tile.typex != 4)
                    {
                        if (this.x + 40 > tile.x * 64 + CAM_X && this.x < tile.x * 64 + CAM_X + 64 && this.y + 40 > tile.y * 64 + CAM_Y && this.y < tile.y * 64 + CAM_Y + 64 && tile.typex != -1 && tile.typex != -3)
                        {
                            CAM_X = this.lx
                            this.xvel = 0
                            if (tile.typex == 5)
                            {
                                flag.active = false
                                CAM_X =  0 + 64 + 64
                                CAM_Y = -640 - 64
                                this.xvel = 0
                                this.yvel = 0
                                tiles.forEach(tile =>{
                                    if (tile.typex == -3)
                                    {
                                        tile.typex = 3
                                    }
                                })
                            }

                        
                        }
                    }

                })
            }
    }
}




