for (var i = 0; i < 200; i++)
{
    tiles.push(new Tile(0, 1, 0, i))
}
for (var i = 0; i < 201; i++)
{
    tiles.push(new Tile(0, 1, 88, i))
}
for (var i = 0; i < 88; i++)
{
    tiles.push(new Tile(0, 1, i, 0))
}
for (var i = 0; i < 88; i++)
{
    tiles.push(new Tile(0, 1, i, 200))
}

for (var i = 1; i < 88; i++)
{
    for (var j = 1; j < 200; j++)
    {
        tiles.push(new Tile(-1, 0, i, j))
    }
}

function findMousePos(event)
{
    mouse.x = event.offsetX
    mouse.y = event.offsetY
}



var types = level
if (types != "")
{
    type = ""
    ptiles = tiles
    tileidx = 0
    tiles.forEach(tile => {
        going = true
        while (going)
        {
            char = types[tileidx]
            if (char == "|")
            {
                type = parseInt(type)
                if (type == -3)
                {
                    type = 3
                }
                tile.SetType(type)
                type = ""
                going = false
            }
            else
            {
                type += char
            }
            tileidx += 1
        }
    })
}

te = 0
ft = 16
ct = Date.now()

function animate(now)
{
    st = ct
    requestAnimationFrame(animate);
    
    ct = Date.now()
    te += ct - st

    if (te >= ft)
    {
        c.clearRect(0, 0, canvas.width, canvas.height);
        
        if (menu == 0)
        {
            c.fillStyle = "navy"
            c.fillRect(0, 0, canvas.width, canvas.height)

            buttons.forEach(button =>
                {
                    button.update(mouse.x, mouse.y, buttons)
                    button.draw()
                    
                })
            c.fillStyle = "white"
            c.font = ("60px Arcade Normal")
            var textWidth = c.measureText("Don't Look Down!").width
            c.fillText("Don't Look Down!", canvas.width / 2 - textWidth / 2, 100)

            leftButton.update(touches)
            leftButton.draw()
        }
        else if (menu == 1)
        {
            b.draw(CAM_Y)
            tiles.forEach(tile => {
            
            
                if (tile.typex != -1)
                    tile.draw()
            })
    
            mario.draw()
            mario.update(tiles)
    
            flag.draw()
            flag.update(mario.x, mario.y)

            c.fillStyle = "white"
            c.font = "30px Arcade Normal"
            c.fillText(time, 20, 40)
    
            time += 1 / 60
            time = Math.round(time * 100) / 100

            leftButton.update(touches)
            leftButton.draw()
        }
        else if (menu == 2)
        {
            c.fillStyle = "mediumseagreen"
            c.fillRect(0, 0, canvas.width, canvas.height)

            c.fillStyle = "white"
            c.font = ("30px Arcade Normal")
            var textWidth = c.measureText("You Completed Don't Look Down! in " + time + " seconds").width
            c.fillText("You Completed Don't Look Down! in " + time + " seconds", canvas.width / 2 - textWidth / 2, 100)
        }

        te = 0 
        
    }
    
}


animate()

canvas.addEventListener('mousemove', findMousePos, false);

addEventListener('keydown', ({keyCode}) => {
    switch(keyCode) {
        case 87:
        //w
            keys.up.pressed = true
            break;
        case 65:
        //a
            keys.left.pressed = true
            break;
        case 83:
        //s
            keys.down.pressed = true
            break;
        case 68:
        //s
            keys.right.pressed = true
            break;
        case 38:
            keys.up.pressed = true
            break;
        case 37:
            keys.left.pressed = true
            break;
        case 40:
            keys.down.pressed = true
            break;
        case 39:
                keys.right.pressed = true
            break;
        case 49:
            block = 0
            break;
        case 50:
            block = 1
            break;
        case 51:
            block = 2
            break;
        case 52:
            block = 3
            break;
        case 53:
            block = 4
            break;
        case 54:
            block = 5
            break;
        case 55:
            block = 6
            break;

        case 48:
            block = -1
            break;
        case 27:
            window.location.reload()
            break;
    }
})
